import React, { Component } from 'react'
import { connect } from 'react-redux'

// IMPORT TABLE ROW
import SessionRow from './SessionRow'

import { Table } from 'semantic-ui-react'

class SessionTable extends Component {

  // RETURNS THE MOST RECENT 10 SESSION OBJECTS
  recent10Sessions = () => {
    let sortedSessionsArr;
    let last10Sessions;

    sortedSessionsArr = this.props.sessions.sort((a,b) => {
      let sessionA = new Date(a.date)
      let sessionB = new Date(b.date)
      return sessionB - sessionA
    })

    last10Sessions = sortedSessionsArr.slice(0, 10)
    return last10Sessions
  }

  // RETURNS HOURLY FLOAT ROUNDED TO THE NEAREST TWO DECIMAL PLACE
  hourlyCalculator = () => {
    const sessionsArr = this.props.sessions
    let totalAmount;
    let totalHours;

    let amountObjectArr = sessionsArr.map(session => session.amount)
    let hourObjectArr = sessionsArr.map(session => session.hours)

    const reducer = (sum, currentValue) => sum + currentValue
    totalAmount = amountObjectArr.reduce(reducer, 0)
    totalHours = hourObjectArr.reduce(reducer, 0)

    let hourly = totalAmount/totalHours
    let twoDecimalHourly = Math.round(hourly * 100) / 100

    return twoDecimalHourly
  }

  render() {
    return (
      <div>
      <Table singleLine>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Date</Table.HeaderCell>
            <Table.HeaderCell>Location</Table.HeaderCell>
            <Table.HeaderCell>Hours Played</Table.HeaderCell>
            <Table.HeaderCell>Won/Loss</Table.HeaderCell>
            <Table.HeaderCell>Hourly</Table.HeaderCell>
          </Table.Row>
        </Table.Header>

        <Table.Body>
          {this.props.sessions ? this.recent10Sessions().map(session => {
            return (
              <SessionRow session={session}/>
            )
          }) : null
          }
        </Table.Body>
        </Table>
      </div>
    )
  }
}


const mapStateToProps = state => {
  return {
    sessions: state.user.sessions
  }
}

export default connect(mapStateToProps)(SessionTable)
