  //     return sessionB - sessionA
  //   })
  //
  //   last25Transfers = sortedTransactions.slice(0, 25)
  //   return last25Transfers
  // }

  render() {

    return (
      <div >
        <Container fluid>
          <Header as='h2'>Request Inbox</Header>
          <Segment style={{ padding: '0em' }} vertical>
            <Grid celled='internally' columns='equal' stackable>
              <Grid.Row textAlign='center'>
                <Grid.Column style={{ paddingBottom: '2em', paddingTop: '2em' }}>
                  SENT REQUESTS

                </Grid.Column>
                <Grid.Column style={{ paddingBottom: '2em', paddingTop: '2em' }}>
                  RECEIVED REQUESTS

                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Segment>
        </Container>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    // currentUser: state.currentUser,
    // sent_requests: state.sent_requests,
    // received_requests: state.received_requests
  }
}

export default connect(mapStateToProps, {})(RequestContainer);
