const URL = 'http://localhost:3001/api/v1/sessions'

const postedSession = data => {
  return {
    type: 'POST_SESSION',
    payload: data
  };
}

const postingSession = sessionObject => {
  return dispatch => {
    dispatch(postedSession(sessionObject))
    fetch(URL, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(sessionObject)
    })
  }
}

const updatedSession = data => {
  return {
    type: 'POST_SESSION',
    payload: data
  };
}

const updatingSession = sessionObject => {
  return dispatch => {
    dispatch(updatedSession(sessionObject))
    fetch(`${URL}/${sessionObject.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(sessionObject)
    })
  }
}


export { postingSession, updatingSession };
