import React, { Component } from 'react'
import { connect } from 'react-redux'
import { } from '../../../redux/actions/sessions.js'

import { Button, Modal, Icon, Form } from 'semantic-ui-react'

class DepositConfirmation extends Component {
  constructor() {
    super()
    this.state = {
      deposit: 0,

      open:false
    }
  }

  componentDidMount() {
    this.setState({
      deposit: 0,
    })
  }

  // SETS STATE ONCHANGE OF FORM INPUT FORMS
  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleUpdate = () => {
    this.props.updatingSession(this.state)

    this.handleToggle()
  }

  handleDelete = () => {
    this.props.deletingSession(this.state)

    this.handleToggle()
  }

  handleToggle = () => {
    this.setState({
      open: !this.state.open
    })
  }

  render() {
    return(
      <Modal open={this.state.open} size='large' trigger={<Button onClick={this.handleToggle}>Edit</Button>}>
        <Modal.Header>Edit Transaction</Modal.Header>
        <Modal.Content>

        </Modal.Content>
        <Modal.Actions>
          <Button color='green' onClick={this.handleUpdate}>Deposit</Button>
          <Button color='grey' onClick={this.handleToggle}><Icon name='cancel'/>Cancel</Button>
        </Modal.Actions>
      </Modal>
    )
  }
}


export default connect(null, { })(DepositConfirmation)
