import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { Container, Search } from 'semantic-ui-react'
import UserCard from './UserCard.js'

class UserList extends Component {
  constructor() {
    super()
    this.state={
      search:'',
      loading: false,
      sortedList: []
    }
  }

  handleChange = event => {
    this.setState({
      search: event.target.value
    }, () => this.handleSearch())
  }

  toggleLoading = event => {
    this.setState({
      loading: !this.state.loading
    })
  }

  handleSearch = () => {
    let filteredList;

    filteredList = this.props.UserList.filter(userObject => {
      if (userObject.username.includes(this.state.search) || userObject.first_name.includes(this.state.search) || userObject.last_name.includes(this.state.search)) {
        return true
      } else {
        return false
      }
    })

    debugger
  }

  render() {
    return(
      <Fragment>
        <Search onSearchChange={this.handleChange} open={false} loading={this.state.loading} onKeyDown={this.toggleLoading} onKeyUp={this.toggleLoading}/>
        <Container>
          People You Might Know
          {this.props.userList ? this.props.userList.map(user => (
            <UserCard user={user} />
          )) : null}
        </Container>
      </Fragment>
    )
  }
}

const mapStateToProps = state => {
  return {
    userList: state.userList
  }
}

export default connect(mapStateToProps, { })(UserList);
