import React, { Component } from 'react';
import { connect } from 'react-redux'
import './App.css';

import NavBar from './NavBar.js'
import Footer from './Footer.js'
// import Home from './react/home/Home.js'
import Login from './react/login/Login.js'
import Dashboard from './react/homepage/Dashboard.js'
import TransferHome from './react/transfer/TransferHome.js'
import Bank from './react/transfer/bankContainer/Bank.js'
import TransferForm from './react/transfer/transferFormContainer/TransferForm.js'
import CasinoMap from './react/casinoMap/CasinoMap.js'

import { Switch, Route } from 'react-router-dom'

import { Container } from 'semantic-ui-react'

class App extends Component {
  constructor() {
    super()
    this.state={
      loading: true
    }
  }

  componentDidUpdate() {
    // debugger
    if (this.props.currentUser.id && this.state.loading) {
      this.setState({
        loading: false
      })
    }
  }

  render() {
    return (
      <div className="App">
        <NavBar />
        <Switch>
        <Container text style={{ marginTop: '7em' }}>
          <Route exact path='/users/transferhome' render={props => {
            return <TransferHome />
          }} />
          <Route exact path='/users/bank' render={props => {
            return <Bank />
          }} />
          <Route exact path='/users/transfer' render={props => {
            return <TransferForm />
          }} />
          <Route exact path='/users' render={props => {
            return <Dashboard />
          }} />
          <Route exact path='/login' component={Login} />
          <Route exact path='/map' component={CasinoMap} />
        </Container>
        </Switch>
        <Footer />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    currentUser: state.currentUser,
  }
}

export default connect(mapStateToProps)(App);
