import React, { Component } from 'react';
import { connect } from 'react-redux'

import { creatingUser } from '../../redux/actions/users.js'

import { Form, Button } from 'semantic-ui-react'

class SignUp extends Component {
  constructor() {
    super()
    this.state = {
      username:'',
      first_name: '',
      last_name: '',
      password: '',
      balance: ''
    }
  }

  clearFormState = () => {
    this.setState({
      username:'',
      first_name: '',
      last_name: '',
      password: '',
      balance: ''
    })
  }

  // SETS STATE ONCHANGE OF FORM INPUT FORMS
  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    })

  }

  onSubmit = event => {
    event.preventDefault()
    let userObject = {
      username:'',
      first_name: '',
      last_name: '',
      password: '',
      balance: ''
    }
    this.clearFormState()
    this.props.creatingUser(userObject)
  }


  render() {
    return (
      <Form onSubmit={this.onSubmit}>
        <Form.Group>
          <Form.Field width={5}>
            <label>Date</label>
            <input
              name='date'
              type='date'
              placeholder='Date...'
              value={this.state.date}
              onChange={this.handleChange}
            />
          </Form.Field>
          <Form.Field width={11}>
            <label>Location</label>
            <input
              name='location'
              placeholder='Location...'
              value={this.state.location}
              onChange={this.handleChange}
            />
          </Form.Field>
        </Form.Group>
        <Form.Group widths='equal'>
          <Form.Field>
            <label>Hours</label>
            <input
              name='hours'
              type='number'
              step='.5'
              placeholder='Hours Played...'
              value={this.state.hours}
              onChange={this.handleChange}
            />
          </Form.Field>
          <Form.Field>
            <label>Bought In</label>
            <input
              name='buyin'
              type='number'
              min='0'
              placeholder='$'
              value={this.state.buyin}
              onChange={this.handleChange}
            />
          </Form.Field>
          <Form.Field>
            <label>Cashed Out</label>
            <input
              name='cashout'
              type='number'
              min='0'
              placeholder='$'
              value={this.state.cashout}
              onChange={this.handleChange}
            />
          </Form.Field>
        </Form.Group>
        <Button type='submit'>Submit</Button>
      </Form>
    )
  }
}

export default connect(null, { creatingUser })(SignUp);
