import _ from 'lodash'
import React, { Component } from 'react'
import { Search, Grid, Header, Segment } from 'semantic-ui-react'

export default class NavBarSearchBar extends Component {
  constructor() {
    super()
    this.state={
      isLoading: false,
      results: [],
      value: ''
    }
  }

  componentWillMount() {
    this.resetComponent()
  }

  resetComponent = () => this.setState({ isLoading: false, results: [], value: '' })

  handleResultSelect = (e, { result }) => this.setState({ value: result.title })

  handleSearchChange = (e, { value }) => {
    this.setState({ isLoading: true, value })

    console.log(this.props.source)

    setTimeout(() => {
      if (this.state.value.length < 1) {
        this.resetComponent()
      }

      let results = this.props.source.filter(userObject => {
        if (userObject.username.toLowerCase().includes(this.state.value.toLowerCase()) || userObject.first_name.toLowerCase().includes(this.state.value.toLowerCase()) || userObject.last_name.toLowerCase().includes(this.state.value.toLowerCase())) {
          return true
        } else {
          return false
        }
      })

      debugger

      this.setState({
        isLoading: false,
        results: results
      })
    }, 300)
  }

  render() {

    return (
      <Grid>
        <Grid.Column width={6}>
          <Search
            loading={this.state.isLoading}
            onResultSelect={this.handleResultSelect}
            onSearchChange={_.debounce(this.handleSearchChange, 500, { leading: true })}
            results={this.state.results}
            value={this.state.value}
            {...this.props}
          />
        </Grid.Column>
      </Grid>
    )
  }
}
