import React, { Component } from 'react'

import { Container, Header } from 'semantic-ui-react'

class UserProfileName extends Component {

  // RETURNS OBJECT'S FULL NAME STRING
  fullNameConverter = (object) => {
    return this.props.currentUser.first_name + ' ' + this.props.currentUser.last_name
  }

  render() {
    return (
      <div>
        {this.props ?
          <Container>
            <Header as='h1'>{this.fullNameConverter()}</Header>
            <Header as='h2'>Username: {this.props.username}</Header>
          </Container> : null}
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    currentUser: state.currentUser,
  }
}

export default connect(mapStateToProps)(UserProfileName)
