import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Button } from 'semantic-ui-react'
// import { fetchingSelectedProfile  } from '../../../redux/actions/users.js'
import TransferHistoryContainer from '../transfercontainer/TransferHistoryContainer.js'
import ProfileInformation from './ProfileInformation'
import PayForm from './PayForm'
import RequestForm from './RequestForm'

class ProfilePageContainer extends Component {
  componentDidMount() {
    this.fetchingSelectedProfile(this.props.id)
  }

  fetchingSelectedProfile = (id) => {
    let token = localStorage.getItem('token')
    debugger
    return (dispatch) => {
      fetch(`${URL}/${id.id}`, {
        method: 'GET',
        headers: {
          "Authentication" : `Bearer ${token}`,
          "selectedProfile": `${id}`
        }
      })
      .then(res => res.json())
      .then(data => {
        debugger
        dispatch(fetchedSelectedProfile(data))
      })
    }
  }

  render() {
    debugger
    return(
      this.state ?
      <div>
        <Button onClick={this.handleClear}> Back </Button>
        <PayForm />
        <RequestForm />
        <ProfileInformation username={this.props.selectedProfile.username} first_name={this.props.selectedProfile.first_name} last_name={this.props.selectedProfile.last_name}/>
        <TransferHistoryContainer sent_transactions={this.props.sent_transactions} received_transactions={this.props.received_transactions} />
      </div>:
      <div>
      </div>
    )
  }
}


export default connect(mapStateToProps, { fetchingSelectedProfile })(ProfilePageContainer);
