import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Route} from 'react-router-dom'

import TransferHistoryContainer from './transfercontainer/TransferHistoryContainer.js'
import TransferProfile from './TransferProfile'
import DepositForm from './depositContainer/DepositForm.js'
import TransferForm from './transferFormContainer/TransferForm.js'

import { fetchingUserData } from '../../redux/actions/users.js'

class TransferHome extends Component {
  componentDidMount() {
    this.props.fetchingUserData(this.props.id)
  }

  render() {
    return(
      <div>
        <TransferProfile />

        <Route exact path='/users/:id/transferhome' render={props => {
          return <TransferHistoryContainer />
        }} />


        <Route exact path='/users/:id/transferhome/deposit' render={props => {
          return <DepositForm />
        }} />

        <Route exact path='/users/:id/transferhome/transfer' render={props => {
          return <TransferForm />
        }} />
      </div>
    )
  }
}


export default connect(null, { fetchingUserData })(TransferHome);
