import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Button, Header, Modal, Icon, Form } from 'semantic-ui-react'

class SessionModal extends Component {
  // constructor() {
  //   super()
  //   this.state = {
  //     date: this.props.sessionObject.date,
  //     location: this.props.sessionObject.location,
  //     hours: this.props.sessionObject.hours,
  //     buyin: '',
  //     cashout: '',
  //     amount: this.props.sessionObject.amount
  //   }
  // }

  handleUpdate = () => {

  }

  render() {
    return(
      <Modal size='large' trigger={<Button>Edit</Button>}>
        <Modal.Header>Edit Transaction</Modal.Header>
        <Modal.Content>
          <Form onSubmit={this.onSubmit}>
            <Form.Group>
              <Form.Field width={5}>
                <label>Date</label>
                <input
                  name='date'
                  type='date'
                  placeholder='Date...'
                  value={this.props.session.date}
                />
              </Form.Field>
              <Form.Field width={11}>
                <label>Location</label>
                <input
                  name='location'
                  placeholder='Location...'
                  value={this.prop.session.location}
                />
              </Form.Field>
            </Form.Group>
            <Form.Group widths='equal'>
              <Form.Field>
                <label>Hours</label>
                <input
                  name='hours'
                  type='number'
                  step='.5'
                  placeholder='Hours Played...'
                  value={this.prop.session.hours}
                />
              </Form.Field>
              <Form.Field>
                <label>Bought In</label>
                <input
                  name='buyin'
                  type='number'
                  min='0'
                  placeholder='$'
                  // value={this.state.buyin}
                  // onChange={this.handleChange}
                />
              </Form.Field>
              <Form.Field>
                <label>Cashed Out</label>
                <input
                  name='cashout'
                  type='number'
                  min='0'
                  placeholder='$'
                  // value={this.state.cashout}
                  // onChange={this.handleChange}
                />
              </Form.Field>
              <Form.Field>
                <label>Won/Loss</label>
                {this.prop.session.amount}
              </Form.Field>
            </Form.Group>
            <Button type='submit'>Submit</Button>
          </Form>
        </Modal.Content>
        <Button color='green'><Icon name='save'/>Save</Button>
        <Button color='red'> <Icon name='trash'/> Delete</Button>
      </Modal>
    )
  }
}


export default SessionModal
